import { ReactComponentElement, useState, useEffect } from "react";
import "./Slider.css";

const Slider = () => {
  const [products, setProducts] = useState([]);
  const [leftPosition, setLeftPosition] = useState(0);
  let itemWidth = window.screen.width / 3; // Adjust this value as needed
  const containerWidth = itemWidth * 7 + (24 * 7 + 32 * 2 + 70); // Number of items

  const onLeftClick = () => {
    let left = leftPosition + itemWidth;
    if (left > 0) {
      left = 0;
    }
    setLeftPosition(left);
  };

  const onRightClick = () => {
    let left = leftPosition - itemWidth;
    if (left < -containerWidth + window.innerWidth) {
      left = -containerWidth + window.innerWidth;
    }
    setLeftPosition(left);
  };

  const [currentCategory, setCurrentCategory] = useState("All");

  const getAllProducts = async () => {
    try {
      const responce = await fetch("http://localhost:5000/products");
      const json_responce = await responce.json();
      setProducts(json_responce);
    } catch (error) {
      console.log(error.message);
    }
  };
  let categories = [];
  products.map((product) => {
    if (!categories.includes(product.category)) {
      categories.push(product.category);
    }
  });

  useEffect(() => {
    getAllProducts();
    // console.log(products);
    // console.log("useffect worked");
  }, []);

  return (
    <>
      <h1 className="text-center mt-5 mb-4">On Sale, up to 90% off </h1>
      <div className="container-fluid mb-5 overflow-hidden px-5 position-relative">
        <div style={{ width: containerWidth }}>
          <button
            type="button"
            className="slider-button-left slider-button"
            onClick={onLeftClick}
          >
            <i className="bi bi-arrow-left"></i>
          </button>
          <button
            type="button"
            className="slider-button-right slider-button"
            onClick={onRightClick}
          >
            <i className="bi bi-arrow-right"></i>
          </button>
          <div
            className="d-flex flex-row position-relative slider-container"
            style={{ left: leftPosition, gap: "2rem" }}
          >
            <div class="card">
              <img
                class="img-fluid"
                alt="100%x280"
                src="https://images.unsplash.com/photo-1532781914607-2031eca2f00d?ixlib=rb-0.3.5&amp;q=80&amp;fm=jpg&amp;crop=entropy&amp;cs=tinysrgb&amp;w=1080&amp;fit=max&amp;ixid=eyJhcHBfaWQiOjMyMDc0fQ&amp;s=7c625ea379640da3ef2e24f20df7ce8d"
              />
              <div class="card-body">
                <h4 class="card-title">Special title treatment</h4>
                <p class="card-text">
                  With supporting text below as a natural lead-in to additional
                  content.
                </p>
              </div>
            </div>
            <div class="card">
              <img
                class="img-fluid"
                alt="100%x280"
                src="https://images.unsplash.com/photo-1532771098148-525cefe10c23?ixlib=rb-0.3.5&amp;q=80&amp;fm=jpg&amp;crop=entropy&amp;cs=tinysrgb&amp;w=1080&amp;fit=max&amp;ixid=eyJhcHBfaWQiOjMyMDc0fQ&amp;s=3f317c1f7a16116dec454fbc267dd8e4"
              />
              <div class="card-body">
                <h4 class="card-title">Special title treatment</h4>
                <p class="card-text">
                  With supporting text below as a natural lead-in to additional
                  content.
                </p>
              </div>
            </div>
            <div class="card">
              <img
                class="img-fluid"
                alt="100%x280"
                src="https://images.unsplash.com/photo-1532715088550-62f09305f765?ixlib=rb-0.3.5&amp;q=80&amp;fm=jpg&amp;crop=entropy&amp;cs=tinysrgb&amp;w=1080&amp;fit=max&amp;ixid=eyJhcHBfaWQiOjMyMDc0fQ&amp;s=ebadb044b374504ef8e81bdec4d0e840"
              />
              <div class="card-body">
                <h4 class="card-title">Special title treatment</h4>
                <p class="card-text">
                  With supporting text below as a natural lead-in to additional
                  content.
                </p>
              </div>
            </div>
            <div class="card">
              <img
                class="img-fluid"
                alt="100%x280"
                src="https://images.unsplash.com/photo-1506197603052-3cc9c3a201bd?ixlib=rb-0.3.5&amp;q=80&amp;fm=jpg&amp;crop=entropy&amp;cs=tinysrgb&amp;w=1080&amp;fit=max&amp;ixid=eyJhcHBfaWQiOjMyMDc0fQ&amp;s=0754ab085804ae8a3b562548e6b4aa2e"
              />
              <div class="card-body">
                <h4 class="card-title">Special title treatment</h4>
                <p class="card-text">
                  With supporting text below as a natural lead-in to additional
                  content.
                </p>
              </div>
            </div>

            <div class="card">
              <img
                class="img-fluid"
                alt="100%x280"
                src="https://images.unsplash.com/photo-1507525428034-b723cf961d3e?ixlib=rb-0.3.5&amp;q=80&amp;fm=jpg&amp;crop=entropy&amp;cs=tinysrgb&amp;w=1080&amp;fit=max&amp;ixid=eyJhcHBfaWQiOjMyMDc0fQ&amp;s=ee8417f0ea2a50d53a12665820b54e23"
              />
              <div class="card-body">
                <h4 class="card-title">Special title treatment</h4>
                <p class="card-text">
                  With supporting text below as a natural lead-in to additional
                  content.
                </p>
              </div>
            </div>
            <div class="card">
              <img
                class="img-fluid"
                alt="100%x280"
                src="https://images.unsplash.com/photo-1532777946373-b6783242f211?ixlib=rb-0.3.5&amp;q=80&amp;fm=jpg&amp;crop=entropy&amp;cs=tinysrgb&amp;w=1080&amp;fit=max&amp;ixid=eyJhcHBfaWQiOjMyMDc0fQ&amp;s=8ac55cf3a68785643998730839663129"
              />
              <div class="card-body">
                <h4 class="card-title">Special title treatment</h4>
                <p class="card-text">
                  With supporting text below as a natural lead-in to additional
                  content.
                </p>
              </div>
            </div>
            <div class="card">
              <img
                class="img-fluid"
                alt="100%x280"
                src="https://images.unsplash.com/photo-1532763303805-529d595877c5?ixlib=rb-0.3.5&amp;q=80&amp;fm=jpg&amp;crop=entropy&amp;cs=tinysrgb&amp;w=1080&amp;fit=max&amp;ixid=eyJhcHBfaWQiOjMyMDc0fQ&amp;s=5ee4fd5d19b40f93eadb21871757eda6"
              />
              <div class="card-body">
                <h4 class="card-title">Special title treatment</h4>
                <p class="card-text">
                  With supporting text below as a natural lead-in to additional
                  content.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Slider;
