import React from "react";
import { useStateContext } from "../context/StateContext";

const Navbar = ({ classNamePosition = "" }) => {
  const { setShowCart, signOut } = useStateContext();
  return (
    <>
      <nav
        className={`py-3 ${
          classNamePosition.length > 0 ? classNamePosition : "fixed-top"
        } navbar navbar-expand-lg bg-dark navbar-dark`}
      >
        <div className="container">
          <a href="/" className="navbar-brand">
            <span className="text-warning">Lion</span> Heart
          </a>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navmenu"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navmenu">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <a href="/#about" className="nav-link">
                  {" "}
                  About{" "}
                </a>
              </li>
              <li className="nav-item">
                <a href="#new" className="nav-link">
                  {" "}
                  New{" "}
                </a>
              </li>
              <li className="nav-item">
                <a href="#clients" className="nav-link">
                  {" "}
                  Clients{" "}
                </a>
              </li>
              <li className="nav-item">
                <a href="#product" className="nav-link">
                  {" "}
                  Product{" "}
                </a>
              </li>
            </ul>
            <div
              className="mx-0 mx-lg-5"
              id="openCart-icon"
              onClick={() => setShowCart(true)}
            >
              <i className="text-warning h5 bi bi-cart-dash"></i>
            </div>
            <div className="mx-0 " onClick={signOut}>
              <a href="/login">
                <i class="h4 text-danger bi bi-box-arrow-right"></i>
              </a>
            </div>
          </div>
        </div>
      </nav>
    </>
  );
};

export default Navbar;
