import { ReactComponentElement, useState, useEffect } from "react";
import "./Slider.css";
import ProductOnSale from "./ProductOnSale";
import { useProductsContext } from "../context/ProductsContext";

const Slider = () => {
  const [leftPosition, setLeftPosition] = useState(0);
  const { products } = useProductsContext();

  let itemWidth = window.screen.width / 4;
  const containerWidth = itemWidth * products.length + (24 * 7 + 32 * 2 + 100);

  const onLeftClick = () => {
    let left = leftPosition + itemWidth * 4;
    if (left > 0) {
      left = 0;
    }
    setLeftPosition(left);
  };

  const onRightClick = () => {
    let left = leftPosition - itemWidth * 4;
    const maxLeft = -containerWidth + window.innerWidth - itemWidth;

    if (left < maxLeft) {
      left = maxLeft;
    }
    setLeftPosition(left);
  };

  const visibleProducts = useMemo(() => {
    // Calculate the range of visible products based on the leftPosition
    const startIndex = Math.floor(-leftPosition / itemWidth);
    const endIndex = Math.min(
      startIndex + Math.ceil(window.innerWidth / itemWidth) + 1, // Add 1 to ensure one extra item is loaded
      products.length
    );

    return products.slice(startIndex, endIndex);
  }, [leftPosition, itemWidth, products]);

  return (
    <>
      <h1 className="text-center mt-5 mb-4">On Sale, up to 90% off </h1>
      <div className="container-fluid mb-5 overflow-hidden px-5 position-relative">
        <div style={{ width: "auto" }}>
          <button
            type="button"
            className="slider-button-left slider-button"
            onClick={onLeftClick}
          >
            <i className="bi bi-arrow-left"></i>
          </button>
          <button
            type="button"
            className="slider-button-right slider-button"
            onClick={onRightClick}
          >
            <i className="bi bi-arrow-right"></i>
          </button>
          <div
            className="d-flex flex-row position-relative slider-container"
            style={{ left: leftPosition, gap: "2rem" }}
          >
            {visibleProducts.map((product) => (
              <ProductOnSale
                key={product.product_id}
                id={product.product_id}
                img={product.img_url}
                name={product.product_name}
                desc={product.description}
                price={product.price}
                category={product.category}
              ></ProductOnSale>
            ))}
          </div>
        </div>
      </div>
    </>
  );
};

export default Slider;
