import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Modal from "react-bootstrap/Modal";

function ViewProducModal({ show, setShow, product }) {
  const [changedImg, setChangedImg] = useState();

  const [formData, setFormData] = useState({
    id: product.id || "",
    name: product.product_name || "",
    img: product.img_url || "",
    desc: product.description || "",
    price: product.price || 0,
  });

  const { id, name, desc, price } = formData;

  const handleClose = () => setShow(false);
  const handleSave = () => {
    // Handle saving the changes, e.g., by sending an API request.
    // You can access the updated values in formData state.
    console.log("Saving changes:", formData);
    handleClose();
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };
  const handleChangeImg = (e) => {
    const file = e.target.files[0];
    console.log(file);
    setChangedImg(file);
  };

  return (
    <>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3" controlId="formProductName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                name="name"
                value={name}
                onChange={handleChange}
                autoFocus
              />
            </Form.Group>
            <img
              src={
                changedImg
                  ? changedImg
                  : "http://localhost:5000/images/" + formData.img
              }
              className="w-50 h-50 text-center"
              alt=""
            />
            <Form.Group className="mb-3" controlId="formProductImg">
              <Form.Label>Image</Form.Label>
              <Form.Control
                type="file"
                name="img"
                onChange={handleChangeImg}
                accept="image/*"
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formProductDesc">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="desc"
                value={desc}
                onChange={handleChange}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formProductPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                name="price"
                value={price}
                onChange={handleChange}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="secondary" onClick={handleClose}>
            Delete Product
          </Button>
          <Button variant="primary" onClick={handleSave}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default ViewProducModal;
