import "./App.css";
import Navbar from "./sections/Navbar";
import Hero from "./sections/Hero";
import SaleTimer from "./components/SaleTimer";
import Products from "./sections/Products";
import Showcase from "./sections/Showcase";
import TopProduct from "./sections/TopProduct";
import Clients from "./sections/Clients";
import Services from "./components/Services";
import Subscribe from "./sections/Subscribe";
import Footer from "./sections/Footer";
import { React, useState } from "react";
import Cart from "./components/Cart";

const CartItems = createContext([]);

function App() {
  const [cartOpen, setCartOpen] = useState(false);

  return (
    <>
      <Navbar></Navbar>
      {cartOpen && <Cart setCartOpen={setCartOpen}></Cart>}
      <Hero></Hero>
      <SaleTimer></SaleTimer>
      <Showcase></Showcase>
      <CartItems.Provider value={{ cartItems, setCartItems }}>
        <Products setCartOpen={setCartOpen}></Products>
      </CartItems.Provider>
      <Services></Services>
      <Subscribe></Subscribe>
      <Clients></Clients>
      <TopProduct></TopProduct>
      <Footer></Footer>
    </>
  );
}

export default App;
