import React from "react";
import heroImgUrl from "../img/hero.jpg";
const Hero = () => {
  return (
    <>
      <section
        class=" text-light d-flex justify-content-center align-items-center  p-5 pt-lg-3 p-lg-0 text-center text-sm-start"
        style={{
          backgroundSize: "cover",
          backgroundImage: `url(${heroImgUrl})`,
          height: "100vh",
        }}
      >
        <div class="container">
          <div class="d-sm-flex align-items-center justify-content-between">
            <div>
              <h1>
                Buy the <span class="text-warning">Best Products.</span>
              </h1>
              <p class="lead my-4">
                We provide the best products in the market. With the best
                quality and the best price.
              </p>
              <a href="#start-journey">
                <button class="btn btn-danger btn-lg">
                  Start your journey
                </button>
              </a>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Hero;
