import {
  React,
  createContext,
  useContext,
  useEffect,
  useMemo,
  useCallback,
  useState,
} from "react";
import { useStateContext } from "./StateContext";

const Context = createContext();

export const ProductsContext = ({ children }) => {
  const { user } = useStateContext();
  const [products, setProducts] = useState([]);
  const getAllProducts = useCallback(async () => {
    try {
      const responce = await fetch("http://localhost:5000/products");
      const json_responce = await responce.json();
      setProducts(json_responce);
      console.log("get all products", json_responce);
    } catch (error) {
      console.log(error.message);
    }
  }, [products]);
  const getUserFavorites = useCallback(async () => {
    try {
      const responce = await fetch(
        `http://localhost:5000/user_favorites${user.user_id}`,
        {
          method: "GET",
        }
      );
      const json_responce = await responce.json();
      // console.log("get user favorites", json_responce);
      return json_responce;
    } catch (error) {
      console.log(error.message);
    }
  }, []);
  useEffect(() => {
    getAllProducts();
    // console.log(getUserFavorites())
    console.log("get user favorites", getUserFavorites());
  }, []);
  return <Context.Provider value={{ products }}>{children}</Context.Provider>;
};

export const useProductsContext = () => useContext(Context);

export default ProductsContext;
