import { React, useContext } from "react";
import { useStateContext } from "../context/StateContext";
const Product = ({ name, img, desc, price }) => {
  const { addToCart, setShowCart } = useStateContext();

  console.log(cartItems);
  const addToCartHandlier = () => {
    addToCart({ name, img, desc, price }, 1);
    setShowCart(true);
  };
  return (
    <div className="col-6 col-md-3">
      <div className="card h-100" style={{ width: "18rem" }}>
        <img src={img} className="card-img-top" alt="Jacket" />
        <div className="card-body">
          <h5 className="card-title d-flex justify-content-between">
            {name}
            <span className="text-danger">{price}$</span>
          </h5>
          <p className="card-text">{desc}</p>
          <a className="btn btn-primary" onClick={addToCartHandlier}>
            Add to Cart
          </a>
        </div>
      </div>
    </div>
  );
};

export default Product;
