import {
  React,
  createContext,
  useContext,
  useEffect,
  useMemo,
  useCallback,
  useState,
} from "react";
import { useStateContext } from "./StateContext";

const Context = createContext();

export const ProductsContext = ({ children }) => {
  const { user } = useStateContext();
  const [products, setProducts] = useState([]);
  const getAllProducts = useCallback(async () => {
    try {
      const responce = await fetch("http://localhost:5000/products");
      const json_responce = await responce.json();
      setProducts(json_responce);
    } catch (error) {
      console.log(error.message);
    }
  }, [products]);
  const setUserFavorites = useCallback(async () => {
    try {
      const responce = await fetch(
        `http://localhost:5000/user_favorites/${user.user_id}`,
        {
          method: "GET",
        }
      );
      const json_responce = await responce.json();
      const normalArray = [...json_responce];
      console.log(normalArray);
      const newProducts = products.map((product) => {
        if (normalArray.includes(String(product.product_id))) {
          return { ...product, favorite: true };
        } else {
          return { ...product, favorite: false };
        }
      });
      console.log(newProducts);
      // setProducts((prevProducts) => {
      //   return prevProducts.map((product) => {
      //     if (favoriteProducts.includes(product.product_id)) {
      //       return { ...product, favorite: true };
      //     } else {
      //       return { ...product, favorite: false };
      //     }
      //   });
      // });
    } catch (error) {
      console.log(error.message);
    }
    return favoriteProducts;
  }, []);

  const onFavorite = async (product_id) => {
    try {
      const data = {
        user_id: user.user_id,
        product_id: product_id,
      };
      console.log(data);

      const result = await fetch("http://localhost:5000/favorite", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      const json_responce = result.json();
      const newProducts = products.map((product) => {
        if (product.product_id == product_id) {
          product.favorite = true;
        }
        return product;
      });
      setProducts([...newProducts]);
    } catch (error) {
      console.error(error);
    }
  };

  const unFavorite = async (product_id) => {
    try {
      const data = {
        user_id: user.user_id,
        product_id: product_id,
      };
      const result = await fetch("http://localhost:5000/favorite", {
        method: "DELETE",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      const json_responce = result.json();
      const newProducts = products.map((product) => {
        if (product.product_id == product_id) {
          product.favorite = false;
        }
        return product;
      });
      setProducts([...newProducts]);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    getAllProducts();
    setUserFavorites();
  }, []);
  return (
    <Context.Provider value={{ products, onFavorite, unFavorite }}>
      {children}
    </Context.Provider>
  );
};

export const useProductsContext = () => useContext(Context);

export default ProductsContext;
