import { useRef } from "react";
import { useStateContext } from "../context/StateContext";

const Login = () => {
  const { setUser } = useStateContext();
  const emailInputRef = useRef(null);
  const passwordInputRef = useRef(null);
  const AuthHandlier = async (e) => {
    e.preventDefault();
    const email = emailInputRef.current.value;
    const password = passwordInputRef.current.value;
    const responce = await fetch("http://localhost:5000/users/auth", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        user_email: email,
        user_password: password,
      }),
    });
    if (responce.status === 404) {
      alert("User not found");
    } else {
      const json_responce = await responce.json();
      alert(json_responce.admin);

      setUser(json_responce.admin === "true" ? "admin" : "user");
    }
  };
  return (
    <>
      <section class="vh-100" style={{ backgroundColor: "#508bfc" }}>
        <div class="container py-5 h-100">
          <div class="row d-flex justify-content-center align-items-center h-100">
            <div class="col-12 col-md-8 col-lg-6 col-xl-5">
              <div
                class="card shadow-2-strong"
                style={{ borderRadius: "1rem" }}
              >
                <form onSubmit={AuthHandlier} class="card-body p-5">
                  <h3 class="mb-5 text-center">Sign in</h3>

                  <div class="mb-3">
                    <label for="InputEmail" class="mb-1 form-label text-start">
                      Email address
                    </label>
                    <input
                      type="email"
                      class="form-control"
                      id="InputEmail"
                      aria-describedby="emailHelp"
                      placeholder="Email"
                      ref={emailInputRef}
                    />
                  </div>
                  <div class="mb-3">
                    <label
                      for="InputPassword"
                      class="mb-1 form-label text-start"
                    >
                      Password
                    </label>
                    <input
                      type="password"
                      class="form-control"
                      id="InputPassword"
                      placeholder="Password"
                      ref={passwordInputRef}
                    />
                  </div>

                  <div class="text-center">
                    <button
                      class="btn btn-primary btn-lg btn-block"
                      type="submit"
                    >
                      Login
                    </button>
                  </div>

                  <hr class="my-4" />
                  <div class="text-center">
                    Don't have an account?{" "}
                    <a
                      href="/register"
                      class="text-decoration-none text-black-50"
                    >
                      Sign up
                    </a>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Login;
