import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import Navbar from "../sections/Navbar";
import Footer from "../sections/Footer";
import { useStateContext } from "../context/StateContext";
import { useProductsContext } from "../context/ProductsContext";

function SingleProduct() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const { onFavorite, unFavorite } = useProductsContext();
  const { addToCart, setShowCart } = useStateContext();

  useEffect(() => {
    // Fetch data for the specific product using the id
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/products/${id}`);
        const productData = await response.json();
        setProduct(productData);
      } catch (error) {
        console.error("Error fetching product data:", error);
      }
    };

    fetchData();
  }, [id]);

  if (!product) {
    return <div>Loading...</div>;
  }
  const {
    sale,
    category,
    img_url,
    price,
    description,
    product_id,
    product_name,
  } = product;
  const addToCartHandlier = () => {
    addToCart(
      {
        id: product_id,
        name: product_name,
        img: img_url,
        desc: description,
        price,
        sale: sale,
      },
      1
    );
  };
  // Render your SingleProduct component with the fetched data
  return (
    <>
      <Navbar classNamePosition="position-static" bgDark={true}></Navbar>

      {/* <!-- Product section--> */}
      <section class="py-5">
        <div class="container px-4 px-lg-5 my-5">
          <div class="row gx-4 gx-lg-5 align-items-center">
            <div class="col-md-6">
              <img
                class="card-img-top mb-5 mb-md-0 bg-light"
                src={"http://localhost:5000/images/" + img_url}
                alt="..."
              />
            </div>
            <div class="col-md-6">
              <div class="small mb-1">{category}</div>
              <h1 class="display-5 fw-bolder">{product_name}</h1>
              <div class="fs-5 mb-5">
                <span className="text-danger">
                  {sale > 0 ? (
                    <span>
                      <span className="text-decoration-line-through text-dark mx-1">
                        {price}
                        {"  "}
                      </span>
                      {price - price / sale} $
                    </span>
                  ) : (
                    `${price} $`
                  )}
                </span>
              </div>
              <p class="lead">{product.description}</p>
              <div class="d-flex">
                <input
                  class="form-control text-center me-3"
                  id="inputQuantity"
                  type="num"
                  value="1"
                  style={{ maxWidth: "3rem" }}
                />
                <button
                  class="btn btn-outline-dark flex-shrink-0"
                  type="button"
                >
                  <a className="btn btn-primary" onClick={addToCartHandlier}>
                    Add to Cart
                  </a>
                </button>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* <!-- Related items section--> */}
      <section class="py-5 bg-light">
        <div class="container px-4 px-lg-5 mt-5">
          <h2 class="fw-bolder mb-4">Related products</h2>
          <div class="row gx-4 gx-lg-5 row-cols-2 row-cols-md-3 row-cols-xl-4 justify-content-center">
            <div class="col mb-5">
              <div class="card h-100">
                {/* <!-- Product image--> */}
                <img
                  class="card-img-top"
                  src="https://dummyimage.com/450x300/dee2e6/6c757d.jpg"
                  alt="..."
                />
                {/* <!-- Product details--> */}
                <div class="card-body p-4">
                  <div class="text-center">
                    {/* <!-- Product name--> */}
                    <h5 class="fw-bolder">Fancy Product</h5>
                    {/* <!-- Product price--> */}
                    $40.00 - $80.00
                  </div>
                </div>
                {/* <!-- Product actions--> */}
