import React, { createContext, useContext, useState, useEffect } from "react";

const Context = createContext();

export const StateContext = ({ children }) => {
  const storedUser = JSON.parse(localStorage.getItem("user"));
  const [user, setUser] = useState(storedUser ? storedUser : null);

  const [showCart, setShowCart] = useState(false);
  const storedCartItems = JSON.parse(localStorage.getItem("cartItems"));
  const [cartItems, setCartItems] = useState(
    storedCartItems ? storedCartItems : []
  );
  const storedTotalPrice = JSON.parse(localStorage.getItem("totalPrice"));

  const [totalPrice, setTotalPrice] = useState(
    storedTotalPrice ? storedTotalPrice : 0
  );
  const [searchText, setSearchText] = useState("");

  const signOut = () => {
    setUser(null);
    localStorage.removeItem("user");
    localStorage.removeItem("cartItems");
    localStorage.removeItem("totalPrice");
  };
  const addToCart = (product, quantity) => {
    const checkProductInCart = cartItems.find((item) => item.id === product.id);

    setTotalPrice((prevTotalPrice) => {
      return prevTotalPrice + product.price * quantity;
    });

    if (checkProductInCart) {
      const updatedCartItems = cartItems.map((cartProduct) => {
        if (cartProduct.id === product.id)
          return {
            ...cartProduct,
            quantity: cartProduct.quantity + quantity,
          };
        else {
          return cartProduct;
        }
      });
      setCartItems(updatedCartItems);
    } else {
      product.quantity = quantity;
      setCartItems([...cartItems, { ...product }]);
    }
    localStorage.setItem("cartItems", cartItems);
    localStorage.setItem("totalPrice", totalPrice);
  };

  const onRemove = (id) => {
    let foundProduct = cartItems.find((item) => item.id === id);
    const newCartItems = cartItems.filter((item) => item.id !== id);
    setTotalPrice(
      (prevTotalPrice) =>
        prevTotalPrice - foundProduct.price * foundProduct.quantity
    );
    setCartItems(newCartItems);
  };

  return (
    <Context.Provider
      value={{
        showCart,
        setShowCart,
        cartItems,
        totalPrice,
        addToCart,
        onRemove,
        setCartItems,
        setTotalPrice,
        setSearchText,
        searchText,
        user,
        setUser,
        signOut,
      }}
    >
      {children}
    </Context.Provider>
  );
};

export const useStateContext = () => useContext(Context);
