import React from "react";
import { products } from "../assets/index";
import Product from "../components/Product";
import "./Cart.css";
import { useStateContext } from "../context/StateContext";

const Cart = () => {
  const { showCart, setShowCart, totalPrice, cartItems } = useStateContext();
  if (!showCart) return null;
  return (
    <div id="cart-container" class="shopping-cart-container bg-light">
      <div class="container h-100">
        <div class="row d-flex justify-content-center h-100 py-5">
          <div class="col-10">
            <div class="d-flex justify-content-between align-items-center mb-4">
              <h3 class="fw-normal mb-0 text-black">Shopping Cart</h3>
              <button
                type="button"
                class="close"
                onClick={() => setShowCart(false)}
                aria-label="Close"
              >
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div id="cart-content-items"></div>
            <div class="fw-bold d-flex justify-content-between mb-1">
              Total amount:
              <span class="text-danger" id="cart-price">
                {totalPrice}$
              </span>
            </div>
            <div class="card">
              <button type="button" onclick="pay()" class="btn btn-warning">
                Proceed to Pay
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cart;
