import { ReactComponentElement, useState } from "react";
import "./Slider.css";

const Slider = () => {
  const [leftPosition, setLeftPosition] = useState(0);
  const [rightPosition, setRightPosition] = useState(0);
  let itemWidth = window.screen.width / 7;
  const onLeftClick = () => {
    let left = leftPosition + itemWidth;
    if (left > 0) {
      left = 0;
    }
    setLeftPosition(left);
    console.log("left");
  };
  const onRightClick = () => {
    let left = leftPosition - itemWidth;
    if (left < -itemWidth * 7) {
      left = -itemWidth * 7;
    }
    setLeftPosition(left);
    console.log("right");
  };

  return (
    <div className="container-fluid my-5">
      <div className=" ">
        <button
          type="button"
          className="slider-button-left slider-button"
          onClick={onLeftClick}
        >
          <i class="bi bi-arrow-left"></i>
        </button>
        <button
          type="button"
          className="slider-button-right slider-button"
          onClick={onRightClick}
        >
          <i class="bi bi-arrow-right"></i>
        </button>
        <div
          className="d-flex flex-row  position-absolute"
          style={{ left: leftPosition, right: rightPosition }}
        >
          <div className="col-4 bg-warning rounded slider-item">1</div>
          <div className="col-4 bg-white rounded slider-item">2</div>
          <div className="col-4 bg-primary rounded slider-item">3</div>
          <div className="col-4 bg-success rounded slider-item">4</div>
          <div className="col-4 bg-primary rounded slider-item">5</div>
          <div className="col-4 bg-white rounded slider-item">6</div>
          <div className="col-4 bg-warning rounded slider-item">7</div>
        </div>
      </div>
    </div>
  );
};

export default Slider;
